 if (!defined('BOOTSTRAP')) { die('Access denied'); } if ($_SERVER['REQUEST_METHOD'] == 'POST') { if ($mode == 'm_update') { $params = $_REQUEST; if (!empty($params['additional_products_data'])) { foreach ($params['additional_products_data'] as $product_id => $product_data) { if (isset($params['additional_products_data'][$product_id . '_price']['prices'])) { if (!isset($params['additional_products_data'][$product_id . '_price']['price'])) { $params['additional_products_data'][$product_id . '_price']['price'] = $params['additional_products_data'][$product_id]['price']; } fn_update_product_prices($product_id, $params['additional_products_data'][$product_id . '_price']); } } } return array(CONTROLLER_STATUS_OK, 'products.manage'); } return; }