<?php
 declare(strict_types=1); namespace Tygh\Addons\SdLabels\Labels\AdditionalSettings; use Tygh\Addons\SdLabels\Collection; class AdditionalSettingsCollection extends Collection { public function __construct() { $this->collection = []; } public function addSetting(AdditionalSetting $additional_setting): void { $this->collection[$additional_setting->code] = $additional_setting; } public function getSettingByCode(string $setting_code) { return $this->collection[$setting_code] ?? false; } public function toArray(): array { $additional_settings = []; foreach ($this->collection as $additional_setting) { $additional_settings[] = $additional_setting->toArray(); } return $additional_settings; } } 