<?php
 use Tygh\Enum\YesNo; defined('BOOTSTRAP') or die('Access denied'); function fn_sd_labels_exim_get_product_label($product_id, $label_id) { $check_exists = db_get_field( 'SELECT count(1) FROM ?:product_sd_labels WHERE product_id = ?i AND label_id = ?s', $product_id, $label_id ); return $check_exists ? YesNo::YES : YesNo::NO; } function fn_sd_labels_exim_set_product_label($product_id, $label_id, $data) { $product_labels_repository = Tygh::$app['addons.sd_labels.product_labels']; if (!empty($data) && $data === YesNo::YES) { $product_labels_repository->attachLabel($product_id, $label_id, YesNo::NO); } else { $product_labels_repository->detachLabel($product_id, $label_id); } }